    DECLARE @MaleFirstNameCount   AS INTEGER = (SELECT COUNT(*) FROM @tblfirstName)
    DECLARE @LastNameCount        AS INTEGER = (SELECT COUNT(*) FROM @tblLastName)
    
    DECLARE @StreetNamesCount AS INTEGER = (SELECT COUNT(*) FROM @tbladdresses)
    DECLARE @languagesCount AS INTEGER = (SELECT COUNT(*) FROM [Language])
    DECLARE @citysCount AS INTEGER = (SELECT COUNT(*) FROM [City])
    DECLARE @FirstName            AS NVARCHAR(100)
    DECLARE @LastName             AS NVARCHAR(100)    
    
    DECLARE @DateOfBirth          AS DATETIME
    DECLARE @StreetNumber			AS INTEGER
    DECLARE @Email                AS NVARCHAR(100)
	DECLARE @StreetName                AS NVARCHAR(100)
	DECLARE @Phone                AS NVARCHAR(15)
    DECLARE @LanguageId         AS INTEGER
    DECLARE @CityId               AS INTEGER
  
    
    DECLARE @ClientId             AS INTEGER = 0
    DECLARE @Number                  AS INTEGER = 0
    WHILE @ClientId < 100000
    BEGIN
        SET @ClientId = @ClientId + 1
        
SELECT @FirstName = (SELECT Name FROM @tblfirstName WHERE MaleFirstNameId = (FLOOR(POWER(RAND(),1.5) * @MaleFirstNameCount) + 1));
SET @LastName = (SELECT Name FROM @tblLastName WHERE LastNameId = (FLOOR(POWER(RAND(),1.5) * @LastNameCount) + 1));
SET @DateOfBirth = CONVERT(datetime, '1991-01-11', 126) - FLOOR(POWER(RAND(),1.5)* 365.0 * 80.0);
SET @LanguageId = FLOOR(RAND() * @languagesCount) + 1;
SET @CityId = (FLOOR(POWER(RAND(),1.5) * @citysCount) + 1);
SET @StreetName = (SELECT Name FROM @tbladdresses WHERE AddressId = (FLOOR(POWER(RAND(),1.5) * @StreetNamesCount) + 1));   
SET @StreetNumber = FLOOR(RAND()*RAND()*200)+1;
SET @Phone = (SELECT '0' + CAST(FLOOR(RAND()* 100)+490 AS NVARCHAR(255)) +  '/' + CAST(FLOOR(RAND()* 999999)+1 AS NVARCHAR(255)));
SET @Email = @FirstName + '.' + @LastName + '@gmail.com';

